rule.definition:
load(
  local!chartLabel: "Average Lag Time and Work Time for selected Tasks",
  local!chartInstructions: "Lag time is the absolute delay between assignment and acceptance of the task, while Work time is the absolute elapsed time between acceptance and completion of the task.", 
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 10,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "lagDurationMinutes",
      ascending: false
    )
  ),
  with(
    local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000db61-5af0-8000-4594-010000010000_30797",
      query: #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "taskName", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "lagDurationMinutes", aggregationFunction: "AVG"),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "workDurationMinutes", aggregationFunction: "AVG")
        }),
        logicalExpression: ri!reportLogicalExpression,
        pagingInfo: local!pagingInfo
      )
    ),
    if(
      or(
        local!datasubset.totalCount < 1,
        #"_a-0000d62a-67b3-8000-da6f-010000010000_951"(
          local!datasubset.data
        )
      ),
      {
        #"SYSTEM_SYSRULES_paragraphField"(
          label: local!chartLabel,
          labelPosition: "ABOVE",
          value: #"_a-0000db61-5af0-8000-4594-010000010000_30935",
          readOnly: true
        )
      },
      {
        #"SYSTEM_SYSRULES_barChartField"(
          label: local!chartLabel,
          labelPosition: "ABOVE",
          instructions: local!chartInstructions,
          categories: {
            index(local!datasubset.data, "taskName", null)
          },
          series: {
            #"SYSTEM_SYSRULES_chartSeries"(
              label: "Avg Lag Time",
              data: round(todecimal(index(local!datasubset.data, "lagDurationMinutes", 0))/1440.0),
              color: "VIOLETRED"
            ),
            #"SYSTEM_SYSRULES_chartSeries"(
              label: "Avg Work Time",
              data: round(todecimal(index(local!datasubset.data, "workDurationMinutes", 0))/1440.0),
              color: "GOLD"
            )
          },
          yAxisTitle: "Days",
          stacking: "NORMAL",
          showLegend: true,
          showTooltips: true,
          allowDecimalAxisLabels: true
        )
      }
    )
  )
)
