rule.definition:
= if(
  #"_a-0000d62a-67b3-8000-da6f-010000010000_950"(
    ri!duration
  ),
  todecimal(
    1
  ),
  with(
    local!split1: split(
      tostring(
        ri!duration
      ),
      "::"
    ),
    local!split2: split(
      local!split1[2],
      ":"
    ),
    local!days: tointeger(
      local!split1[1]
    ),
    local!hours: tointeger(
      local!split2[1]
    ),
    local!minutes: tointeger(
      local!split2[2]
    ),
    if(
      local!days + local!hours + local!minutes <= 0,
      todecimal(1),
      todecimal(
        (
          local!days * 1440
        ) + (
          local!hours * 60
        ) + (
          local!minutes
        )
      )
    )
  )
)
