              totalCount: local!datasubset.totalCount,
              columns: {
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Assignee",
                  field: "taskAssignees",
                  data: apply(
                    #"0c91cb01-7278-4a58-a965-ccfaf368ae4f",
                    index(
                      local!datasubset.data,
                      "taskAssignees",
                      {}
                    )
                  )
                ),
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Total # of Tasks",
                  field: "id",
                  data: index(
