                    index(
                      local!datasubset.data,
                      "lagDurationMinutes",
                      {}
                    )
                  ),
                  links: apply(
                    #"SYSTEM_SYSRULES_dynamicLink"(
                      value: _,
                      saveInto: {
                        a!save(
                          local!selectedAssignee,
                          null
                        ),
                        a!save(
                          local!selectedAssignee,
                          save!value
                        ),
                        a!save(
                          local!displayLagChart,
                          true
                        )
                      }
                    ),
                    local!datasubset.data.taskAssignees
                  )
                ),
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Avg Work Time",
                  field: "workDurationMinutes",
                  data: apply(
                    #"_a-0000dc71-e19a-8000-9ba2-011c48011c48_6386",
                    index(
                      local!datasubset.data,
                      "workDurationMinutes",
                      {}
                    )
                  ),
                  links: apply(
                    #"SYSTEM_SYSRULES_dynamicLink"(
                      value: _,
                      saveInto: {
                        a!save(
                          local!selectedAssignee,
                          save!value
                        ),
                        a!save(
                          local!displayLagChart,
                          false
                        )
                      }
                    ),
                    local!datasubset.data.taskAssignees
                  )
                ),
                #"SYSTEM_SYSRULES_gridImageColumn"(
                  label: "Performance",
                  field: "lagDurationMinutes",
                  data: apply(
                    #"SYSTEM_SYSRULES_documentImage"(
                      document: _
                    ),
                    apply(
                      #"_a-0000dc71-e19a-8000-9ba2-011c48011c48_6388"(
                        value: _,
                        firstTierMin: _,
                        secondTierMin: _
                      ),
                      merge(
                        round(
                          todecimal(
                            local!datasubset.data.lagDurationMinutes
