    {
      if(
        local!displayLagChart,
        #"_a-0000dc71-e19a-8000-9ba2-011c48011c48_6390"(
          ri!reportLogicalExpression,
          local!selectedAssignee
        ),
        #"_a-0000dc71-e19a-8000-9ba2-011c48011c48_6391"(
          ri!reportLogicalExpression,
