rule.definition:
load(
  local!chartInstructions: "Task Lag Time Summary",
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 15,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "id",
      ascending: false
    )
  ),
  local!selectedIndex: 0,
  with(
    local!chartLabel: #"0c91cb01-7278-4a58-a965-ccfaf368ae4f"(
      ri!currentAssignee
    ),
    local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000db61-5af0-8000-4594-010000010000_30797",
      query: #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(
          aggregationColumns: {
            #"SYSTEM_SYSRULES_queryAggregationColumn"(
              field: "taskName",
              isGrouping: true
            ),
            #"SYSTEM_SYSRULES_queryAggregationColumn"(
              field: "lagDurationMinutes",
              aggregationFunction: "AVG"
            ),
            #"SYSTEM_SYSRULES_queryAggregationColumn"(
              field: "id",
              aggregationFunction: "COUNT"
            )
          }
        ),
        logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND",
          filters: #"SYSTEM_SYSRULES_queryFilter"(
            field: "taskAssignees",
            operator: "=",
            value: ri!currentAssignee
          ),
          logicalExpressions: ri!reportLogicalExpression
        ),
        pagingInfo: local!pagingInfo
      )
    ),
    {
      if(
        or(
          local!datasubset.totalCount < 1,
          #"_a-0000d62a-67b3-8000-da6f-010000010000_951"(
            local!datasubset.data
          )
        ),
        {
          #"SYSTEM_SYSRULES_paragraphField"(
            label: local!chartLabel,
            labelPosition: "ABOVE",
            value: #"_a-0000db61-5af0-8000-4594-010000010000_30935",
            readOnly: true
          )
        },
        {
          #"SYSTEM_SYSRULES_barChartField"(
            label: local!chartLabel,
            labelPosition: "ABOVE",
            instructions: local!chartInstructions,
            categories: {
              apply(
                fn!concat,
                merge(
                  index(
                    local!datasubset.data,
                    "taskName",
                    null
                  ),
                  repeat(
                    length(
                      local!datasubset.data
                    ),
                    " ("
                  ),
                  index(
                    local!datasubset.data,
                    "id",
                    null
                  ),
                  repeat(
                    length(
                      local!datasubset.data
                    ),
                    ")"
                  )
                )
              )
            },
            series: {
              #"SYSTEM_SYSRULES_chartSeries"(
                label: "Avg Lag Time",
                data: round(
                  todecimal(
                    index(
                      local!datasubset.data,
                      "lagDurationMinutes",
                      0
                    )
                  ) / 1440.0
                ),
                color: "VIOLETRED"
              )
            },
            yAxisTitle: "Days",
            showLegend: true,
            showTooltips: true,
            allowDecimalAxisLabels: true
          )
        }
      )
    }
  )
)
