rule.definition:
load(
  local!chartLabel: "Most frequent Tasks",
  local!chartInstructions: "The most frequently completed tasks given the filtering criteria.", 
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 10,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "id",
      ascending: false
    )
  ),
  with(
    local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000db61-5af0-8000-4594-010000010000_30797",
      query: #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "taskUuid", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "taskName", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "id", aggregationFunction: "COUNT"),
        }),
        logicalExpression: ri!reportLogicalExpression,
        pagingInfo: local!pagingInfo
      )
    ),  
    if(
      or(
        local!datasubset.totalCount <= 0,
        #"_a-0000d62a-67b3-8000-da6f-010000010000_951"(
          local!datasubset.data
        )
      ),
      {
        #"SYSTEM_SYSRULES_paragraphField"(
          label: local!chartLabel,
          labelPosition: "ABOVE",
          value: #"_a-0000db61-5af0-8000-4594-010000010000_30935",
          readOnly: true
        )
      },
      {
        #"SYSTEM_SYSRULES_barChartField"(
          label: local!chartLabel,
          labelPosition: "ABOVE",
          instructions: local!chartInstructions,
          categories: {
            index(local!datasubset.data, "taskName", null)
          },
          series: {
            #"SYSTEM_SYSRULES_chartSeries"(
              label: "Tasks",
              data: index(local!datasubset.data, "id", null),
              color: "SKYBLUE"
            )
          },
          yAxisTitle: "Number of Tasks completed",
          showLegend: false,
          showDataLabels: true,
          showTooltips: true        
        )
      }
    )
  )
)
