rule.definition:
load(
  local!chartLabel: "Task Completion time:",
  local!chartInstructions: "Percentage of Tasks completed within different time intervals. All intervals are mutually exclusive, and each slice is clickable for more information.",
  local!showGrid: toboolean(false),
  local!minBound: 0,
  local!maxBound: 999999999999,
  local!tempDatasubset,
  local!gridPagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 10,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "taskStartTime",
      ascending: false
    )
  ),
  with(
    /* -------------------------------------------------------------------------- */
    /* 5 total intervals are used by default, but they can be altered as needed. */
    /*      -Less than 1 day                                                      */
    /*      -Less than 3 days                                                     */
    /*      -Less than 1 week                                                     */
    /*      -Less than 1 month                                                    */
    /*      -Longer than a month                                                  */
    /* -------------------------------------------------------------------------- */
    local!taskMetricTotals: #"af047e86-62f8-4754-b7be-c1fda0b153c5"(ri!reportLogicalExpression,topaginginfo(1,0)),
    {
      if(not(local!showGrid),
        {
          #"SYSTEM_SYSRULES_pieChartField"(
            label: local!chartLabel,
            labelPosition: "ABOVE",
            instructions: local!chartInstructions,
            series: {
              #"SYSTEM_SYSRULES_chartSeries"(
                label: "<1 day",
                data: local!taskMetricTotals[1],
                links: #"SYSTEM_SYSRULES_dynamicLink"(
                        value: true,
                        saveInto: {
                          local!showGrid,
                          a!save(local!minBound, 0),          /* Absolute lower bound */
                          a!save(local!maxBound, 60 * 24)     /* Minutes in a day     */
                        }
                      ),
                color: "GREEN"
              ),
              #"SYSTEM_SYSRULES_chartSeries"(
                label: "<3 days",
                data: local!taskMetricTotals[2],
                links: #"SYSTEM_SYSRULES_dynamicLink"(
                        value: true,
                        saveInto: {
                          local!showGrid,
                          a!save(local!minBound, 60 * 24),      /* Minutes in a day  */
                          a!save(local!maxBound, 60 * 24 * 3),  /* Minutes in 3 days */
                        }
                      ), 
                color: "YELLOW"
              ),
              #"SYSTEM_SYSRULES_chartSeries"(
                label: "<1 week",
                data: local!taskMetricTotals[3],
                links: #"SYSTEM_SYSRULES_dynamicLink"(
                        value: true,
                        saveInto: {
                          local!showGrid,
                          a!save(local!minBound, 60 * 24 * 3),  /* Minutes in 3 days */
                          a!save(local!maxBound, 60 * 24 * 7)   /* Minutes in a week */
                        }
                      ), 
                color: "BLUEGRAY"
              ),
              #"SYSTEM_SYSRULES_chartSeries"(
                label: "<1 month",
                data: local!taskMetricTotals[4],
                links: #"SYSTEM_SYSRULES_dynamicLink"(
                        value: true,
                        saveInto: {
                          local!showGrid,
                          a!save(local!minBound, 60 * 24 * 7),  /* Minutes in a week          */
                          a!save(local!maxBound, 60 * 24 * 30), /* Approx. minutes in a month */
                        }
                      ),
                color: "PURPLE"
              ),
              #"SYSTEM_SYSRULES_chartSeries"(
                label: ">1 month",
                data: local!taskMetricTotals[5],
                links: #"SYSTEM_SYSRULES_dynamicLink"(
                        value: true,
                        saveInto: {
                          local!showGrid,
                          a!save(local!minBound, 60 * 24 * 30), /* Approx. minutes in a month */
                          a!save(local!maxBound, 999999999999), /* Absolute upper bound       */
                        }
                      ),
                color: "VIOLETRED"
              )
            },
            showDataLabels: true,
            showTooltips: true
          )
        },
        #"1aa5e0ea-3a9e-4b61-a6f5-929280622522"(          
          taskMetricDatasubset: local!tempDatasubset,
          pagingInfo: local!gridPagingInfo,
          reportLogicalExpression: ri!reportLogicalExpression,
          minBound: local!minBound,
          maxBound: local!maxBound,
          showGrid: local!showGrid,
          chartLabel: local!chartLabel,
          chartInstructions: local!chartInstructions
        ) 
      )
    }
  )
)
